import React, { useState } from 'react';
import { Globe, Send, Filter, Users } from 'lucide-react';

// Types
interface MultiLanguageText {
  en: string;
  zh: string;
  vi: string;
  ms: string;
}

interface MenuItem {
  id: number;
  name: MultiLanguageText;
  description?: MultiLanguageText;
  price: number;
  pax?: string;
  allergens?: string[];
  dietary?: string[];
  isSpicy?: boolean;
  isPopular?: boolean;
}

interface Language {
  code: string;
  name: string;
  nativeName: string;
}

interface DietaryFilter {
  id: string;
  label: string;
  icon: string;
}

interface ChatMessage {
  role: 'assistant' | 'user';
  content: string;
}

// Constants
const menuItems: MenuItem[] = [
  {
    id: 1,
    name: {
      en: "Prosperity Yee Sang",
      zh: "ÂèëË¥¢È±ºÁîü",
      vi: "G·ªèi c√° h·ªìi th·ªãnh v∆∞·ª£ng",
      ms: "Yee Sang Kemakmuran"
    },
    description: {
      en: "Cold smoked salmon, cherry compote, murabba, coconut flakes, sengkuang, pomegranate, alam raja, beetroot, lettuce, carrots, white radish, bunga kantan, kaffir lime leaf, pickles, peanut crumbs",
      zh: "ÂÜ∑ÁÜè‰∏âÊñáÈ±ºÈÖçÂêÑÁßçÈÖçÊñô",
      vi: "C√° h·ªìi hun kh√≥i l·∫°nh, m·ª©t anh ƒë√†o, v·ª•n d·ª´a",
      ms: "Salmon salai sejuk dengan pelbagai bahan"
    },
    price: 118,
    pax: "6-8",
    allergens: ['nuts', 'seafood', 'raw'],
    dietary: ['sharing'],
    isSpicy: false,
    isPopular: true
  },
  {
    id: 2,
    name: {
      en: "Ciabatta with Hummus",
      zh: "‰∏≠‰∏úÈù¢ÂåÖÈÖçÈπ∞Âò¥Ë±ÜÊ≥•",
      vi: "B√°nh m√¨ Ciabatta v·ªõi Hummus",
      ms: "Ciabatta dengan Hummus"
    },
    price: 23,
    allergens: ['gluten'],
    dietary: ['vegetarian', 'vegan']
  },
  {
    id: 3,
    name: {
      en: "Spicy Tuna Tartare",
      zh: "È¶ôËæ£ÈáëÊû™È±ºÂ°îÂ°î",
      vi: "G·ªèi c√° ng·ª´ cay",
      ms: "Tartare Tuna Pedas"
    },
    description: {
      en: "Fresh tuna, avocado, sesame oil, crispy shallots, wasabi aioli, house-made chili oil",
      zh: "Êñ∞È≤úÈáëÊû™È±ºÈÖçÁâõÊ≤πÊûúÂíåËäùÈ∫ªÊ≤π",
      vi: "C√° ng·ª´ t∆∞∆°i, b∆°, d·∫ßu m√®",
      ms: "Tuna segar, alpukat, minyak bijan"
    },
    price: 42,
    allergens: ['seafood', 'raw'],
    isSpicy: true,
    isPopular: true
  }
];

const dietaryFilters: DietaryFilter[] = [
  { id: 'vegetarian', label: 'Vegetarian', icon: 'ü•¨' },
  { id: 'vegan', label: 'Vegan', icon: 'üå±' },
  { id: 'halal', label: 'Halal', icon: '‚ò™Ô∏è' },
  { id: 'glutenFree', label: 'Gluten Free', icon: 'üåæ' },
  { id: 'dairyFree', label: 'Dairy Free', icon: 'ü•õ' },
  { id: 'nutFree', label: 'Nut Free', icon: 'ü•ú' },
  { id: 'noSpicy', label: 'No Spicy', icon: 'üå∂Ô∏è' }
];

const languages: Language[] = [
  { code: 'en', name: 'English', nativeName: 'English' },
  { code: 'zh', name: 'Chinese', nativeName: '‰∏≠Êñá' },
  { code: 'vi', name: 'Vietnamese', nativeName: 'Ti·∫øng Vi·ªát' },
  { code: 'ms', name: 'Malay', nativeName: 'Bahasa Melayu' }
];

const MenuApp = () => {
  const [language, setLanguage] = useState<string>('en');
  const [showLanguageSelect, setShowLanguageSelect] = useState(false);
  const [filters, setFilters] = useState<string[]>([]);
  const [showFilters, setShowFilters] = useState(false);
  const [chatHistory, setChatHistory] = useState<ChatMessage[]>([{
    role: 'assistant',
    content: 'How can I help you with our menu today?'
  }]);
  const [question, setQuestion] = useState('');

  const handleChat = () => {
    if (!question.trim()) return;

    const newHistory: ChatMessage[] = [
      ...chatHistory,
      { role: 'user', content: question },
      { role: 'assistant', content: 'Thank you for your question. Our staff will assist you shortly.' }
    ];

    setChatHistory(newHistory);
    setQuestion('');
  };

  const toggleFilter = (filterId: string) => {
    setFilters(prev => 
      prev.includes(filterId)
        ? prev.filter(f => f !== filterId)
        : [...prev, filterId]
    );
  };

  const filterMenuItems = () => {
    return menuItems.filter(item => {
      if (filters.includes('vegetarian') && !item.dietary?.includes('vegetarian')) return false;
      if (filters.includes('vegan') && !item.dietary?.includes('vegan')) return false;
      if (filters.includes('glutenFree') && item.allergens?.includes('gluten')) return false;
      if (filters.includes('dairyFree') && item.allergens?.includes('dairy')) return false;
      if (filters.includes('nutFree') && item.allergens?.includes('nuts')) return false;
      if (filters.includes('noSpicy') && item.isSpicy) return false;
      return true;
    });
  };

  return (
    <div className="flex flex-col h-screen bg-white">
      {/* Header */}
      <div className="p-4 border-b">
        <div className="flex justify-between items-center">
          <h1 className="text-xl font-serif">TABLE & APRON</h1>
          <div className="flex gap-2">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="p-2 rounded-full hover:bg-gray-100"
            >
              <Filter size={20} />
            </button>
            <button 
              onClick={() => setShowLanguageSelect(!showLanguageSelect)}
              className="p-2 rounded-full hover:bg-gray-100"
            >
              <Globe size={20} />
            </button>
          </div>
        </div>
      </div>

      {/* Language Selector */}
      {showLanguageSelect && (
        <div className="absolute top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-sm w-full mx-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium">Select Language</h3>
              <button 
                onClick={() => setShowLanguageSelect(false)}
                className="text-gray-500 hover:text-gray-700"
              >
                ‚úï
              </button>
            </div>
            <div className="grid grid-cols-1 gap-2">
              {languages.map((lang) => (
                <button
                  key={lang.code}
                  onClick={() => {
                    setLanguage(lang.code);
                    setShowLanguageSelect(false);
                  }}
                  className={`p-3 rounded-lg text-left flex items-center justify-between ${
                    language === lang.code 
                      ? 'bg-red-50 text-red-800' 
                      : 'hover:bg-gray-50'
                  }`}
                >
                  <span>{lang.nativeName}</span>
                  <span className="text-sm text-gray-500">({lang.name})</span>
                </button>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Filters */}
      {showFilters && (
        <div className="bg-white p-4 border-b">
          <div className="flex flex-wrap gap-2">
            {dietaryFilters.map(filter => (
              <button
                key={filter.id}
                onClick={() => toggleFilter(filter.id)}
                className={`px-3 py-1 rounded-full text-sm flex items-center gap-1 ${
                  filters.includes(filter.id)
                    ? 'bg-red-800 text-white'
                    : 'bg-gray-100'
                }`}
              >
                {filter.icon} {filter.label}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Menu Items */}
      <div className="flex-1 overflow-auto">
        {filterMenuItems().map((item) => (
          <div key={item.id} className="p-4 border-b">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <h2 className="text-lg font-medium">
                  {item.name[language as keyof MultiLanguageText] || item.name.en}
                </h2>
                {item.description && (
                  <p className="text-gray-600 text-sm mt-1 italic">
                    {item.description[language as keyof MultiLanguageText] || item.description.en}
                  </p>
                )}
                <div className="flex flex-wrap gap-2 mt-2">
                  {item.allergens?.map((allergen, idx) => (
                    <span key={idx} className="px-2 py-1 rounded-full bg-red-50 text-red-800 text-xs">
                      Contains: {allergen}
                    </span>
                  ))}
                  {item.dietary?.map((diet, idx) => (
                    <span key={idx} className="px-2 py-1 rounded-full bg-green-50 text-green-800 text-xs">
                      {diet}
                    </span>
                  ))}
                  {item.pax && (
                    <span className="px-2 py-1 rounded-full bg-blue-50 text-blue-800 text-xs">
                      {item.pax} pax
                    </span>
                  )}
                  {item.isPopular && (
                    <span className="px-2 py-1 rounded-full bg-yellow-50 text-yellow-800 text-xs">
                      ‚≠ê Popular
                    </span>
                  )}
                </div>
              </div>
              <div className="text-right ml-4">
                <span className="font-medium">RM {item.price}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Chat Interface */}
      <div className="border-t p-4">
        <div className="bg-red-50 rounded-lg p-4 mb-4 max-h-48 overflow-y-auto">
          {chatHistory.map((msg, idx) => (
            <div key={idx} className={`mb-2 ${
              msg.role === 'assistant' ? 'text-red-800' : 'text-gray-800'
            }`}>
              {msg.role === 'assistant' ? 'üçΩÔ∏è ' : 'üë§ '}
              {msg.content}
            </div>
          ))}
        </div>

        <div className="flex gap-2">
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleChat()}
            placeholder="Ask about menu items, allergies, or get recommendations..."
            className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-red-800"
          />
          <button 
            onClick={handleChat}
            className="bg-red-800 text-white p-3 rounded-lg hover:bg-red-900"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default MenuApp;
